 Provides a list of selectable, supported chart types.
  
 This sample shows how render column chart. ( method.)
 
   Bunifu.DataViz.Canvas canvas1 = new Bunifu.DataViz.Canvas();
   Bunifu.DataViz.DataPoint datapoint1 = new Bunifu.DataViz.DataPoint(Bunifu.DataViz.BunifuDataViz._type.Bunifu_column);
          {
             Random r = new Random();
             datapoint1.addLabely("SUN", r.Next(0, 100).ToString());
             datapoint1.addLabely("MON", r.Next(0, 100).ToString());
             datapoint1.addLabely("TUE", r.Next(0, 100).ToString());
             datapoint1.addLabely("WED", r.Next(0, 100).ToString());
             datapoint1.addLabely("THU", r.Next(0, 100).ToString());
             datapoint1.addLabely("FRI", r.Next(0, 100).ToString());
             datapoint1.addLabely("SAT", r.Next(0, 100).ToString());
             canvas1.addData(datapoint1);
         }
bunifuDataViz1.Render(canvas1);

